package CAPSTONE.PROJECT.payload;




import jakarta.persistence.Column;
import jakarta.validation.constraints.*;
import org.springframework.boot.context.properties.bind.DefaultValue;

import java.util.List;

public record NewCampeggioDTO (



        Long postiDisp ,
        @NotEmpty(message = "indirizzo obbligatorio")

                String indirizzo ,
        String nome,
        @DefaultValue("data:image/png;base64,")
        List <String> immagini,
        @NotEmpty(message = "email obbligatoria")
        @Pattern(regexp = "^[\\w-\\.]+@([\\w-]+\\.)+[\\w-]{2,4}$", message = "L'email inserita non è valida")
        String email,
        @NotNull(message = "tel obbligatorio")
        Long numeroTelefono,

        boolean caniAmmessi,

        String descrizione,
        boolean piscina,
        boolean animazione,
        boolean market,
        boolean wifi,
        boolean ristorante,


        @Min(value = 0, message = "Il numero minimo di stelle è 1")
        @Max(value = 5, message = "Il numero massimo di stelle è 5")
       Integer stelle,
        @Column(length = 5000)
        String logo,


        @NotEmpty(message = "la password è obbligatoria")
        String password

){ public  NewCampeggioDTO{}


}
